import React from "react"
import '../App.css'
import { useEffect, useState } from "react";
import { ethers } from "ethers"

function Login() {
    // consts
    const [currentAccount, setCurrentAccount] = useState('');
    const [profile, setProfile] = useState([])

    // Implement connectWallet method
    const connectWallet = async () => {
        try {
        const { ethereum } = window;

        if (!ethereum) {
            alert("Get MetaMask -> https://metamask.io/");
            return;
        }

        // method to request access to account.
        const accounts = await ethereum.request({ method: "eth_requestAccounts" });

        // This should print out public address once we authorize Metamask.
        console.log("Connected", accounts[0]);
        const account = accounts[0];
        setCurrentAccount(account);
        } catch (error) {
        console.log(error)
        }
    }

    // Create a function to render if Lens account is not connected yet
    const renderNotConnectedContainer = () => (
        <div>
            <h1>Sparta</h1>
            <p>
            Sparta is a social fitness app built on &nbsp;  
            <a
                className="App-link"
                href="https://lens.xyz/"
                target="_blank"
                rel="noopener noreferrer"
            >
                Lens
            </a> 
            </p>
            <button onClick={""} className='cta-button connect-button'>
            Sign in with Lens
            </button>
            <div className='sub-text'>
                <p>Get your Lens profile &nbsp;
                    <a
                        className="App-link"
                        href="https://claim.lens.xyz/"
                        target="_blank"
                        rel="noopener noreferrer"
                    >here</a>
                </p>
            </div>
        </div>
      );

    return (
        <div className="not-connected-container">
            { renderNotConnectedContainer() }
        </div>
    );
}

export default Login
